CREATE DATABASE JobApp;

USE JobApp;

CREATE TABLE User(
    id              INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
    email           VARCHAR(255) NOT NULL,
    password        VARCHAR(255) NOT NULL,
    accessLevel     CHAR(10) NOT NULL DEFAULT 'user'
);

CREATE TABLE AccessToken(
    id              INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
    userId          INT NOT NULL,
    token           VARCHAR(255) NOT NULL,
    birth           TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    CONSTRAINT c_accesstoken_user
        FOREIGN KEY(userId) REFERENCES User(id)
        ON UPDATE CASCADE
        ON DELETE CASCADE
);

CREATE TABLE Listing(
    id              INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    jobTitle        VARCHAR(255) NOT NULL,
    company         VARCHAR(255) NOT NULL,
    location        VARCHAR(255) NOT NULL,
    description     TEXT NOT NULL,
    requirements    TEXT NOT NULL,
    companyImage    VARCHAR(255),
    postDate        DATETIME NOT NULL DEFAULT NOW(),
    wageIndication VARCHAR(255) NOT NULL
);

CREATE TABLE Application(
    id              INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    userId          INT NOT NULL,
    listingId       INT NOT NULL,
    appDate         DATETIME NOT NULL DEFAULT NOW(),
    CONSTRAINT c_application_user
        FOREIGN KEY(userId) REFERENCES User(id),
    CONSTRAINT c_application_listing
        FOREIGN KEY(listingId) REFERENCES Listing(id)
);

